INSERT INTO `products` (`id`, `category_id`, `title`, `slug`, `short_description`, `price`, `promo_price`, `description`, `display_order`, `created_at`, `updated_at`) VALUES
(1, 7, 'ADE ĐẠM SỮA', 'ade-dam-sua', 'DINH DƯỠNG DẠNG BỘT\r\nADE ĐẠM SỮA – ĐẠM SỮA CAO CẤP, CHỐNG CÒI, NẶNG CÂN, MƯỢT LÔNG, NỞ MÔNG, BUNG ĐÙI, BẬT ỨC', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>ADE ĐẠM SỮA &ndash; ĐẠM SỮA CAO CẤP, CHỐNG C&Ograve;I, NẶNG C&Acirc;N, MƯỢT L&Ocirc;NG, NỞ M&Ocirc;NG, BUNG Đ&Ugrave;I, BẬT ỨC</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Vitamin A, D3, E, Protein v&agrave; một số th&agrave;nh phần bổ sung kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;</p>\r\n\r\n<p>Hiệu quả vỗ b&eacute;o nhanh, xuất chuồng sớm, hạn chế d&ugrave;ng kh&aacute;ng sinh trong chăn nu&ocirc;i gia s&uacute;c, gia cầm</p>\r\n\r\n<p>Bổ sung vitamin, axid amin v&agrave; enzyme cho vật nu&ocirc;i trong trường hợp thức ăn kh&ocirc;ng đủ dưỡng chất, rối loạn trao đổi chất, ti&ecirc;u chảy k&eacute;o d&agrave;i hoặc sau khi chủng ngừa vacccine</p>\r\n\r\n<p>Bổ sung protein một c&aacute;ch c&acirc;n đối. C&oacute; thể bổ sung thay thế cho sữa mẹ khi n&aacute;i mất sữa trong những ng&agrave;y ốm k&eacute;m do vận chuyển xa</p>\r\n\r\n<p>K&iacute;ch th&iacute;ch t&iacute;nh th&egrave;m ăn v&agrave; tăng cường ti&ecirc;u ho&aacute;, hấp thu, cải thiện hiệu quả thức ăn</p>\r\n\r\n<p>Gi&uacute;p gia s&uacute;c, gia cầm ăn nhiều, lớn nhanh, ch&acirc;n v&agrave;ng, l&ocirc;ng b&oacute;ng mượt v&agrave; nặng c&acirc;n</p>\r\n\r\n<p>Tăng khả năng đẻ trứng, tăng chất lượng trứng, l&ograve;ng đỏ nhiều, hạn chế đẻ non</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;, d&ecirc;, cừu, lợn</strong>&nbsp; &nbsp; &nbsp; &nbsp; : 1g/8-10l nước hoặc 1g/2kg thức ăn</p>\r\n\r\n<p><strong>G&agrave;, vịt, ngan, ngỗng</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;:<strong>&nbsp;1g/1l nước hoặc trộn 1g/1kg thức ăn</strong></p>\r\n\r\n<p>Ho&agrave; nước cho uống hoặc trộn với thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 14:30:12', NULL),
(2, 7, 'ADE TRỨNG', 'ade-trung', 'ADE TRỨNG – GIÚP TĂNG NĂNG SUẤT TRỨNG, TĂNG TRỌNG LƯỢNG TRỨNG, VỎ TRỨNG DÀY, LÒNG ĐỎ TO, KÉO DÀI THỜI GIAN ĐẺ TRỨNG Ở GIA CẦM, GIÚP XƯƠNG GIA SÚC, GIA CẦM CHẮC KHOẺ, LÔNG, MÓNG ÓNG MƯỢT VÀ MAU LỚN', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>ADE TRỨNG &ndash; GI&Uacute;P TĂNG NĂNG SUẤT TRỨNG, TĂNG TRỌNG LƯỢNG TRỨNG, VỎ TRỨNG D&Agrave;Y, L&Ograve;NG ĐỎ TO, K&Eacute;O D&Agrave;I THỜI GIAN ĐẺ TRỨNG Ở GIA CẦM, GI&Uacute;P XƯƠNG GIA S&Uacute;C, GIA CẦM CHẮC KHOẺ, L&Ocirc;NG, M&Oacute;NG &Oacute;NG MƯỢT V&Agrave; MAU LỚN</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;vitamin A, D3, E v&agrave; một số th&agrave;nh phần bổ sung kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Tăng tỷ lệ ph&ocirc;i, tỷ lệ ấp nở, tăng sản lượng trứng, ph&ograve;ng sảy thai, đẻ non. Tăng sức đề kh&aacute;ng, tăng cường hệ miễn dịch, chống stress</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, ngan, ngỗng, chim, c&uacute;t</strong>&nbsp; &nbsp; &nbsp; &nbsp;: 1g/1-2l nước hoặc 2g/1-2kg thức ăn</p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;, d&ecirc;, cừu, heo&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/8-10l nước hoặc 1g/2kg thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:03:06', NULL),
(3, 7, 'Bộ W-phage', 'bo-w-phage', 'BỘ W-PHAGE – KHÔNG PHẢI LÀ THUỐC KHÁNG SINH\r\nBẮT VI KHUẨN E.COLI, SALMONELLA, CLOSTRIDIUM (2 TRONG 1)', 0.00, NULL, '<p><strong>BỘ W-PHAGE &ndash; KH&Ocirc;NG PHẢI L&Agrave; THUỐC KH&Aacute;NG SINH</strong></p>\r\n\r\n<p><strong>BẮT VI KHUẨN E.COLI, SALMONELLA, CLOSTRIDIUM (2 TRONG 1)</strong></p>\r\n\r\n<p><strong>GỒM BỘ 2 SẢN PHẨM:</strong></p>\r\n\r\n<p><strong>1.<a href=\"https://winpharma.com.vn/product/w-phage/\">W-PHAGE</a></strong>: Bắt vi khuẩn E.coli, Salmonella, Clostridium</p>\r\n\r\n<p><strong>2.<a href=\"https://winpharma.com.vn/product/w-nutri/\">W-NUTRI</a></strong>: K&iacute;ch th&iacute;ch sản sinh thực khuẩn thể mới nhanh ch&oacute;ng tăng lực, k&iacute;ch th&iacute;ch tăng trưởng, tăng cường hệ miễn dịch chống lại mầm bệnh</p>\r\n', 0, '2025-11-12 15:04:03', NULL),
(4, 7, 'BV-BỔ GAN THẬN', 'bv-bo-gan-than', 'BV-BỔ GAN THẬN – GIẢI ĐỘC, MÁT GAN, TIÊU HOÁ TỐT, KÍCH THÍCH THÈM ĂN, TĂNG CƯỜNG HỆ MIỄN DỊCH', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>BV-BỔ GAN THẬN &ndash; GIẢI ĐỘC, M&Aacute;T GAN, TI&Ecirc;U HO&Aacute; TỐT, K&Iacute;CH TH&Iacute;CH TH&Egrave;M ĂN, TĂNG CƯỜNG HỆ MIỄN DỊCH</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Vitamin C, B1, Mangnesium, Lysine, Methionine v&agrave; một số th&agrave;nh phần bổ sung kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;</p>\r\n\r\n<p>Tăng cường chức năng giải độc, tiết mật, ngừa chứng kh&oacute; ti&ecirc;u, t&aacute;o b&oacute;n, ph&acirc;n sống</p>\r\n\r\n<p>K&iacute;ch th&iacute;ch th&egrave;m ăn, n&acirc;ng cao hiệu quả sử dụng thức ăn, tổng hợp protein gi&uacute;p gia s&uacute;c, gia cầm mau lớn, đẻ nhiều</p>\r\n\r\n<p>T&aacute;i tạo tế b&agrave;o gan, phục hồi sức khoẻ v&agrave; tăng cường hệ miễn dịch ở gia s&uacute;c, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, ngan, ngỗng, thỏ, c&uacute;t, bồ c&acirc;u</strong>&nbsp; &nbsp; &nbsp; &nbsp; : 1g/1l nước uống hoặc 1kg/300-400kg thức ăn</p>\r\n\r\n<p><strong>Heo, d&ecirc;, cừu, b&ecirc;, ngh&eacute;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/2l nước uống hoặc 1.5kg/1 tấn thức ăn</p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;, ngựa</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:<strong>&nbsp;1g/5l nước uống hoặc 1kg/1 tấn thức ăn</strong></p>\r\n\r\n<p>Ho&agrave; với nước hoặc trộn với thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:06:30', NULL),
(5, 7, 'CALCIUM-PLUS', 'calcium-plus', 'CALCIUM-PLUS – ĐẺ KHOẺ, TRỨNG TO, VỎ DÀY, CHẮC XƯƠNG, MƯỢT LÔNG, PHÒNG BẠI LIỆT', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>CALCIUM-PLUS &ndash; ĐẺ KHOẺ, TRỨNG TO, VỎ D&Agrave;Y, CHẮC XƯƠNG, MƯỢT L&Ocirc;NG, PH&Ograve;NG BẠI LIỆT</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Canxi, Biotin, Vitamin D3, Mangnesium, Manganese, Iron, Copper, Zinc, Cobalt v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;</p>\r\n\r\n<p>Bổ sung men vi sinh gi&uacute;p gia s&uacute;c, gia cầm ổn định c&acirc;n bằng hệ vi sinh vật đường ruột, hấp thụ thức ăn triệt để, cải thiện FCR</p>\r\n\r\n<p>Ngừa bệnh E.coli, thương h&agrave;n, vi&ecirc;m ruột hoại tử tr&ecirc;n g&agrave;, vịt, ngan, c&uacute;t</p>\r\n\r\n<p>Ph&ograve;ng v&agrave; xử l&yacute; c&aacute;c bệnh thường gặp tr&ecirc;n gia s&uacute;c, gia cầm như: bệnh ph&acirc;n trắng lợn con trong giai đoạn theo mẹ, bệnh sưng ph&ugrave; đầu của lợn con, bệnh ph&acirc;n sống do rối loạn ti&ecirc;u ho&aacute;, chống rối loạn ti&ecirc;u ho&aacute; do sử dụng kh&aacute;ng sinh tr&ecirc;n gia s&uacute;c, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>B&ograve; sữa đang khai th&aacute;c</strong>&nbsp; &nbsp; &nbsp; &nbsp;: 2g/1l nước uống hoặc trộn thức ăn 100g/con/ng&agrave;y</p>\r\n\r\n<p><strong>B&ograve; sữa mang thai</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 3g/l nước uống, li&ecirc;n tục 2 tuần trước khi sinh</p>\r\n\r\n<p><strong>Gia cầm, heo</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:<strong>&nbsp;1-2g/ 1l nước hoặc trộn 100g/50-100kg thức ăn</strong></p>\r\n\r\n<p>Ho&agrave; với nước hoặc trộn với thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:07:08', NULL),
(6, 7, 'ĐIỆN GIẢI C', 'dien-giai-c', 'ĐIỆN GIẢI C – GIẢI NHIỆT, TĂNG LỰC, CHỐNG NÓNG, CHỐNG MẤT NƯỚC, TĂNG SỨC ĐỀ KHÁNG, NGĂN NGỪA STRESS', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>ĐIỆN GIẢI C &ndash; GIẢI NHIỆT, TĂNG LỰC, CHỐNG N&Oacute;NG, CHỐNG MẤT NƯỚC, TĂNG SỨC ĐỀ KH&Aacute;NG, NGĂN NGỪA STRESS</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Vitamin K3, C, Natri v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;</p>\r\n\r\n<p>Bổ sung điện giải v&agrave; vitamin C, K cho gia s&uacute;c, gia cầm</p>\r\n\r\n<p>Chống n&oacute;ng, tăng cường giải độc</p>\r\n\r\n<p>Cầm m&aacute;u, tăng t&iacute;nh bền vững th&agrave;nh mạch</p>\r\n\r\n<p>Hỗ trợ ph&ograve;ng v&agrave; xử l&yacute; c&aacute;c bệnh: Cầu tr&ugrave;ng, Gumboro, ti&ecirc;u chảy, E.coli</p>\r\n\r\n<p>Chống stress khi thay đổi thời tiết, chủng vacxin, cắt mỏ, vận chuyển gia s&uacute;c, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Gia s&uacute;c</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 100g/50l nước hoặc 100g/70kg thức ăn</p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;:<strong>&nbsp;100g/100l nước hoặc 100g/50kg thức ăn</strong></p>\r\n\r\n<p>Ho&agrave; nước uống hoặc trộn với thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:07:41', NULL),
(7, 7, 'GLUCO K C', 'gluco-k-c', 'GLUCO K C – CẦM MÁU, THANH NHIỆT, GIẢI ĐỘC, NÂNG CAO SỨC ĐỀ KHÁNG, TĂNG CƯỜNG HỆ MIỄN DỊCH', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>GLUCO K C &ndash; CẦM M&Aacute;U, THANH NHIỆT, GIẢI ĐỘC, N&Acirc;NG CAO SỨC ĐỀ KH&Aacute;NG, TĂNG CƯỜNG HỆ MIỄN DỊCH</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Vitamin K3, C v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong></p>\r\n\r\n<p>Giải độc, chống n&oacute;ng, chống xuất huyết, giải nhiệt cho gia s&uacute;c, gia cầm.&nbsp;</p>\r\n\r\n<p>Tăng lực, chống suy kiệt cho lợn n&aacute;i sau sinh.</p>\r\n\r\n<p>Sản phẩm đặc biệt hiệu quả trong hỗ trợ v&agrave; xử l&yacute; c&aacute;c bệnh truyền nhiễm kế ph&aacute;t như PRRS (tai xanh, sốt đỏ) ở lợn, Gumboro, k&yacute; sinh tr&ugrave;ng đường m&aacute;u ở g&agrave;</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, ngan, ngỗng, chim, c&uacute;t</strong>&nbsp; &nbsp; : 1g/1l nước hoặc 2g/1kg thức ăn</p>\r\n\r\n<p><strong>Lợn, tr&acirc;u, b&ograve;, d&ecirc;, cừu&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 100g/50l nước hoặc 100g/50-60kg thức ăn</p>\r\n\r\n<p>Ho&agrave; với nước hoặc trộn với thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:08:08', NULL),
(8, 7, 'GLUCO K-C AMIN THẢO DƯỢC', 'gluco-k-c-amin-thao-duoc', 'GLUCO K-C AMIN THẢO DƯỢC – CUNG CẤP CHẤT ĐIỆN GIẢI, BÙ NƯỚC CHO VẬT NUÔI KHI VẬT NUÔI BỊ SỐT CAO, TIÊU CHẢY', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>GLUCO K-C AMIN THẢO DƯỢC &ndash; CUNG CẤP CHẤT ĐIỆN GIẢI, B&Ugrave; NƯỚC CHO VẬT NU&Ocirc;I KHI VẬT NU&Ocirc;I BỊ SỐT CAO, TI&Ecirc;U CHẢY</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Vitamin C, Lysine, Vitamin K3, Methionine v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;N&acirc;ng cao sức đề kh&aacute;ng, tăng cường hệ miễn dịch, tăng khả năng kh&aacute;ng bệnh, giải nhiệt, chống n&oacute;ng, chống mất nước. Tăng lực, ngăn ngừa stress.</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Gia s&uacute;c</strong>&nbsp; &nbsp; &nbsp; &nbsp; : 100g/50l nước hoặc 100g/70kg thức ăn</p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp;:<strong>&nbsp;100g/100l nước hoặc 100g/50kg thức ăn.</strong></p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:08:42', NULL),
(9, 7, 'GLUCO KC-GINSENG', 'gluco-kc-ginseng', 'GLUCO KC-GINSENG – GIÚP TĂNG CƯỚNG SỨC ĐỀ KHÁNG, TĂNG CƯỜNG HẤP THU THỨC ĂN, TIÊU HOÁ TỐT', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>GLUCO KC-GINSENG &ndash; GI&Uacute;P TĂNG CƯỚNG SỨC ĐỀ KH&Aacute;NG, TĂNG CƯỜNG HẤP THU THỨC ĂN, TI&Ecirc;U HO&Aacute; TỐT</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Vitamin C, K3 v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;N&acirc;ng cao sức đề kh&aacute;ng, tăng cường hệ miễn dịch, giải độc, chống n&oacute;ng, chống xuất huyết, cầm m&aacute;u, giải nhiệt</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, chim, c&uacute;t, ngan, ngỗng</strong>&nbsp; &nbsp; : 1g/1l nước hoặc 2g/1kg thức ăn</p>\r\n\r\n<p><strong>Lợn, tr&acirc;u, b&ograve;, d&ecirc;, cừu&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 100g/50l nước hoặc 100g/50-60kg thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:09:37', NULL),
(10, 7, 'LACTO C', 'lacto-c', 'LACTO C – BỔ SUNG VITAMIN, AXIT AMIN VÀ VI SINH VẬT CÓ LỢI GIÚP CON VẬT TĂNG TÍNH THÈM ĂN, TĂNG TRỌNG NHANH, MAU LỚN, CÂN BẰNG HỆ VI SINH VẬT ĐƯỜNG RUỘT, GIÚP TĂNG SỨC ĐỀ KHÁNG, GIẢM NHIỆT CHO GIA SÚC, GIA CẦM', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>LACTO C &ndash; BỔ SUNG VITAMIN, AXIT AMIN V&Agrave; VI SINH VẬT C&Oacute; LỢI GI&Uacute;P CON VẬT TĂNG T&Iacute;NH TH&Egrave;M ĂN, TĂNG TRỌNG NHANH, MAU LỚN, C&Acirc;N BẰNG HỆ VI SINH VẬT ĐƯỜNG RUỘT, GI&Uacute;P TĂNG SỨC ĐỀ KH&Aacute;NG, GIẢM NHIỆT CHO GIA S&Uacute;C, GIA CẦM</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Bacillus subtilis, Vitamin C, B2, B3 v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Tăng cường hệ miễn dịch, n&acirc;ng cao sức đề kh&aacute;ng, tăng khả năng hấp thụ c&aacute;c chất dinh dưỡng, giảm stress. N&acirc;ng cao hiệu quả khai th&aacute;c trứng ở gia cầm đẻ</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Lợn con tập ăn, cai sữa, b&ecirc;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 100g/30kg thức ăn</p>\r\n\r\n<p><strong>Gia s&uacute;c kh&aacute;c</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1kg/400kg thức ăn</p>\r\n\r\n<p><strong>Gia cầm ( G&agrave;, vịt, ngan, ngỗng, c&uacute;t)</strong>&nbsp; &nbsp;&nbsp;:<strong>&nbsp;100g/50kg thức ăn</strong></p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:10:15', NULL),
(11, 7, 'MEN MUTIZYM', 'men-mutizym', 'MUTIZYM – KÍCH THÍCH TIÊU HOÁ, PHÒNG NGỪA TIÊU CHẢY, PHÂN KHÔ, GIẢM MÙI HÔI', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>MUTIZYM &ndash; K&Iacute;CH TH&Iacute;CH TI&Ecirc;U HO&Aacute;, PH&Ograve;NG NGỪA TI&Ecirc;U CHẢY, PH&Acirc;N KH&Ocirc;, GIẢM M&Ugrave;I H&Ocirc;I</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Bacillus subtillis, Bacillus cereus v&agrave; một số th&agrave;nh phần kh&aacute;c&nbsp;</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;</p>\r\n\r\n<p>Bổ sung men vi sinh gi&uacute;p gia s&uacute;c, gia cầm ổn định c&acirc;n bằng hệ vi sinh vật đường ruột, hấp thu thức ăn triệt để, cải thiện FCR</p>\r\n\r\n<p>Ngừa bệnh E.coli, thương h&agrave;n, vi&ecirc;m ruột hoại tử tr&ecirc;n g&agrave;, vịt, ngan, ngỗng</p>\r\n\r\n<p>Ph&ograve;ng v&agrave; xử l&yacute; c&aacute;c bệnh thường gặp tr&ecirc;n gia s&uacute;c, gia cầm như bệnh ph&acirc;n trắng lợn con trong giai đoạn theo mẹ, bệnh sưng ph&ugrave; đầu của lợn con, bệnh ph&acirc;n sống do rối loạn ti&ecirc;u ho&aacute;</p>\r\n\r\n<p>Chống rối loạn ti&ecirc;u ho&aacute; khi sử dụng kh&aacute;ng sinh cho gia s&uacute;c, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Gia s&uacute;c kh&aacute;c&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp;: 1kg/400kg thức ăn</p>\r\n\r\n<p><strong>Heo con, b&ecirc;, ngh&eacute;&nbsp; &nbsp; &nbsp;</strong>&nbsp; : 1kg/400kg thức ăn</p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;:<strong>&nbsp;1klg/200kg thức ăn</strong></p>\r\n\r\n<p>Ho&agrave; nước hoặc trộn với thức ăn, sử dụng li&ecirc;n tục trong suốt thời gian nu&ocirc;i gia s&uacute;c</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:10:39', NULL),
(12, 7, 'MEN SỐNG TAN', 'men-song-tan', 'MEN SỐNG TAN – GIÚP KÍCH THÍCH TIÊU HOÁ, CHUYỂN HOÁ THỨC ĂN GIÚP LỢN, TRÂU, BÒ, GÀ, VỊT, NGAN, CÚT TĂNG TRƯỞNG NHANH, MAU LỚN, GIÚP CÂN BẰNG HỆ VI SINH VẬT ĐƯỜNG RUỘT', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>MEN SỐNG TAN &ndash; GI&Uacute;P K&Iacute;CH TH&Iacute;CH TI&Ecirc;U HO&Aacute;, CHUYỂN HO&Aacute; THỨC ĂN GI&Uacute;P LỢN, TR&Acirc;U, B&Ograve;, G&Agrave;, VỊT, NGAN, C&Uacute;T TĂNG TRƯỞNG NHANH, MAU LỚN, GI&Uacute;P C&Acirc;N BẰNG HỆ VI SINH VẬT ĐƯỜNG RUỘT</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Bacillus subtilis, Vitamin B1, B6 v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;K&iacute;ch th&iacute;ch ti&ecirc;u ho&aacute;, giảm ti&ecirc;u tốn thức ăn, tăng trọng nhanh, ngăn ngừa c&aacute;c bệnh g&acirc;y rối loạn tri&ecirc;u ho&aacute;, n&acirc;ng cao sức đề kh&aacute;ng, tăng cường hệ miễn dịch</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Heo con, b&ecirc;, ngh&eacute;&nbsp;</strong>&nbsp;&nbsp; &nbsp;: 1kg/200-300kg thức ăn</p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;:<strong>&nbsp;1kg/200kg thức ăn</strong></p>\r\n\r\n<p><strong>Gia s&uacute;c kh&aacute;c</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1kg/400kg thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:11:05', NULL),
(13, 7, 'PROBIOTICS', 'probiotics', 'PROBIOTICS – KÍCH THÍCH TIÊU HOÁ, GIẢM TIÊU TỐN THỨC ĂN, NGỪA TIÊU CHẢY', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>PROBIOTICS &ndash; K&Iacute;CH TH&Iacute;CH TI&Ecirc;U HO&Aacute;, GIẢM TI&Ecirc;U TỐN THỨC ĂN, NGỪA TI&Ecirc;U CHẢY</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Bacillus subtillis, Methionine, Lysine, Vitamin C, B1, B6 v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Cung cấp vi sinh vật sống c&oacute; lợi cho gia s&uacute;c, gia cầm. Ngăn ngừa c&aacute;c bệnh g&acirc;y rối loạn ti&ecirc;u ho&aacute;. K&iacute;ch th&iacute;ch ti&ecirc;u ho&aacute;, giảm ti&ecirc;u tốn thức ăn, tăng trọng nhanh. N&acirc;ng cao sức đề kh&aacute;ng, tăng cường hệ miễn dịch, giảm stress.</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Lợn con tập ăn, cai sữa, b&ecirc;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 100g/30kg thức ăn</p>\r\n\r\n<p><strong>Gia s&uacute;c kh&aacute;c&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1kg/400kg thức ăn&nbsp;</p>\r\n\r\n<p><strong>Gia cầm (g&agrave;, vịt, ngan, ngỗng, c&uacute;t)</strong>&nbsp; &nbsp; &nbsp;:<strong>&nbsp;100g/50kg thức ăn</strong></p>\r\n\r\n<p>Ho&agrave; với nước hoặc trộn với thức ăn, sử dụng li&ecirc;n tục trong suốt thời gian nu&ocirc;i gia s&uacute;c gia cầm</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:11:33', NULL),
(14, 7, 'SIÊU GIẢI ĐỘC AMINOVITOL', 'sieu-giai-doc-aminovitol', 'AMINOVITOL – GIẢI ĐỘC GAN THẬN CẤP, TĂNG CƯỜNG TRAO ĐỔI CHẤT, KÍCH THÍCH ĂN NHIỀU, TĂNG TRỌNG NHANH', 0.00, NULL, '<p><strong>THUỐC UỐNG DẠNG LỎNG</strong></p>\r\n\r\n<p><strong>AMINOVITOL &ndash; GIẢI ĐỘC GAN THẬN CẤP, TĂNG CƯỜNG TRAO ĐỔI CHẤT, K&Iacute;CH TH&Iacute;CH ĂN NHIỀU, TĂNG TRỌNG NHANH</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Sorbitol, Lysine, Methionine, Vitamin B6, Bổ sung vitamin, kho&aacute;ng, probiotic; Bổ sung enzyme, th&agrave;nh phần thảo dược</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;</p>\r\n\r\n<p>Bổ gan, giải độc gan, phục hồi chức năng gan, k&iacute;ch th&iacute;ch tiết mật, tiết dịch vị d&ugrave;ng để hỗ trợ điều trị v&agrave; ph&ograve;ng chống c&aacute;c bệnh nhiễm độc gan, vi&ecirc;m t&uacute;i mật, kh&oacute; ti&ecirc;u, chướng bụng, đầy hơi, ỉa chảy, ph&acirc;n sống, t&aacute;o b&oacute;n, dị ứng</p>\r\n\r\n<p>Ph&ograve;ng v&agrave; hỗ trợ xử l&yacute; c&aacute;c bệnh tho&aacute;i ho&aacute; gan, t&iacute;ch mỡ trong c&aacute;c trường hợp vi&ecirc;m gan cấp, sơ gan</p>\r\n\r\n<p>Tăng cường qu&aacute; tr&igrave;nh trao đổi chất, k&iacute;ch th&iacute;ch tạo m&aacute;u, tăng trường nhanh, l&ocirc;ng mượt, hồng da</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Tr&acirc;u b&ograve;, d&ecirc;, cừu, lợn&nbsp; &nbsp;&nbsp;</strong>:10ml/50-70kg thể trọng&nbsp;</p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;:<strong>1-2ml/ 1l nước</strong></p>\r\n\r\n<p>Ho&agrave; nước cho uống hoặc trộn với thức ăn li&ecirc;n tục trong suốt thời gian nu&ocirc;i gia s&uacute;c</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:12:02', NULL),
(15, 7, 'SIÊU MEN VIT', 'sieu-men-vit', 'MEN TIÊU HOÁ CAO CẤP, KÍCH THÍCH TIÊU HOÁ TĂNG, HẤP THỤ THỨC ĂN, PHÒNG NGỪA TIÊU CHẢY, PHÂN SỐNG', 0.00, NULL, '<p><strong>THUỐC UỐNG DẠNG LỎNG</strong></p>\r\n\r\n<p><strong>MEN TI&Ecirc;U HO&Aacute; CAO CẤP, K&Iacute;CH TH&Iacute;CH TI&Ecirc;U HO&Aacute; TĂNG, HẤP THỤ THỨC ĂN, PH&Ograve;NG NGỪA TI&Ecirc;U CHẢY, PH&Acirc;N SỐNG</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Bacillu, Natri, Kali v&agrave; một số th&agrave;nh phần bổ sung kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;</p>\r\n\r\n<p>K&iacute;ch th&iacute;ch ti&ecirc;u ho&aacute;, tăng tỉ lệ chuyển ho&aacute; thức ăn, tăng trọng nhanh, r&uacute;t ngắn thời gian nu&ocirc;i</p>\r\n\r\n<p>Bổ sung vi khuẩn c&oacute; lợi cho đường ti&ecirc;u ho&aacute;, c&acirc;n bằng hệ vi sinh vật đường ruột</p>\r\n\r\n<p>Kiểm so&aacute;t ti&ecirc;u chảy do E.coli, thương h&agrave;n, sưng ph&ugrave; đầu</p>\r\n\r\n<p>Tạo ph&acirc;n khu&ocirc;n, kh&ocirc;, giảm m&ugrave;i h&ocirc;i v&agrave; &ocirc; nhiễm chuồng nu&ocirc;i</p>\r\n\r\n<p>Tăng tiết sữa cho lợn n&aacute;i</p>\r\n\r\n<p>Tăng chất lượng trừng, tăng tỉ lệ ph&ocirc;i v&agrave; tỉ lệ ấp nở cao</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Gia cầm&nbsp;&nbsp;</strong>: 1ml/1l nước uống hoặc 100ml/80-100kg thức ăn</p>\r\n\r\n<p><strong>Gia s&uacute;c</strong>&nbsp; &nbsp;:<strong>&nbsp;2ml/1l nước uống hoặc 100ml/120-150kg thức ăn</strong></p>\r\n\r\n<p>Pha nước hoặc trộn với thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:12:28', NULL),
(16, 7, 'SIÊU MỌC LÔNG BV-BIOTIN', 'sieu-moc-long-bv-biotin', 'BV-BIOTIN – SIÊU TĂNG TRỌNG, KÍCH THÍCH MỌC LÔNG, CHỐNG CẮN MỔ, VỖ BÉO, CHỐNG CÒI', 0.00, NULL, '<p><strong>DINH ĐƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>BV-BIOTIN &ndash; SI&Ecirc;U TĂNG TRỌNG, K&Iacute;CH TH&Iacute;CH MỌC L&Ocirc;NG, CHỐNG CẮN MỔ, VỖ B&Eacute;O, CHỐNG C&Ograve;I</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Vitamin B1, B2, Biotin, Vitamin B6, Kẽm, Lysine, Vitamin D3, Canxi v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;</p>\r\n\r\n<p>K&iacute;ch th&iacute;ch mọc l&ocirc;ng, chống cắn mổ</p>\r\n\r\n<p>Bổ sung vitamin thiết yếu v&agrave; kho&aacute;ng chất vi lượng cho gia cầm</p>\r\n\r\n<p>Vỗ b&eacute;o, chống c&ograve;i, tăng sản lượng thịt, tăng số lượng v&agrave; chất lượng trứng</p>\r\n\r\n<p>Tạo m&agrave;u v&agrave;ng đẹp cho l&ograve;ng đỏ trứng</p>\r\n\r\n<p>N&acirc;ng cao năng suất cho gia cầm sinh sản</p>\r\n\r\n<p>Trứng to, vỏ d&agrave;y, đẻ cao, k&eacute;o d&agrave;i</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, ngan, ngỗng, chim, c&uacute;t</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 100g/100l nước hoặc 100g/50-100kg thức ăn</p>\r\n\r\n<p><strong>Lợn n&aacute;i, lợn con&nbsp;&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 20g/20-30kg thức ăn</p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;, d&ecirc;, cừu, b&ecirc;, ngh&eacute;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:<strong>&nbsp;20g/50kg thức ăn</strong></p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:13:01', NULL),
(17, 7, 'SIÊU VỖ BÉO PAKWAY-NEW', 'sieu-vo-beo-pakway-new', 'PAKWAY-NEW – BỔ SUNG AXIT AMIN, VITAMIN CHO GIA SÚC, GIA CẦM KHOẺ MẠNH, LỚN NHANH, KÍCH THÍCH TIÊU HOÁ, NÂNG CAO SỨC ĐỀ KHÁNG, TĂNG TRỌNG NHANH, CHỐNG CÒI XƯƠNG, BẠI LIỆT, NGOẠI HÌNH ĐẸP, TĂNG CHẤT LƯỢNG THỊT KHI XUẤT BÁN', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>PAKWAY-NEW &ndash; BỔ SUNG AXIT AMIN, VITAMIN CHO GIA S&Uacute;C, GIA CẦM KHOẺ MẠNH, LỚN NHANH, K&Iacute;CH TH&Iacute;CH TI&Ecirc;U HO&Aacute;, N&Acirc;NG CAO SỨC ĐỀ KH&Aacute;NG, TĂNG TRỌNG NHANH, CHỐNG C&Ograve;I XƯƠNG, BẠI LIỆT, NGOẠI H&Igrave;NH ĐẸP, TĂNG CHẤT LƯỢNG THỊT KHI XUẤT B&Aacute;N</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Vitamin B1, B6, Natri, Cu, Lysine, Methionine v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Si&ecirc;u vỗ b&eacute;o ở gia cầm. Ngăn ngừa c&ograve;i cọc, chậm lớn, ph&ograve;ng chống bại liệt ở gia s&uacute;c. Tăng cường n&acirc;ng cao sức đề kh&aacute;ng ở vật nu&ocirc;i.</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, chim, c&uacute;t, ngan</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/2l nước uống hoặc 100g/1 tấn thể trọng</p>\r\n\r\n<p><strong>Lợn v&agrave; gia s&uacute;c</strong>&nbsp; &nbsp; : 100g/2 tấn thể trọng hoặc 100g/25kg thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:13:33', NULL),
(18, 7, 'SORBITOL COMPLEX', 'sorbitol-complex', 'SORBITOL COMPLEX – GIÚP BỔ GAN, LỢI MẬT, TĂNG TÍNH THÈM ĂN, TĂNG KHẢ NĂNG TIÊU HOÁ, GIÚP VẬT NUÔI ĂN NHIỀU, MAU LỚN. GIÚP TĂNG CHỨC NĂNG GIẢI ĐỘC CỦA GAN, GIÚP GIẢM NGỘ ĐỘC DO ĐỘC TỐ CỦA NẤM MỐC', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>SORBITOL COMPLEX &ndash; GI&Uacute;P BỔ GAN, LỢI MẬT, TĂNG T&Iacute;NH TH&Egrave;M ĂN, TĂNG KHẢ NĂNG TI&Ecirc;U HO&Aacute;, GI&Uacute;P VẬT NU&Ocirc;I ĂN NHIỀU, MAU LỚN. GI&Uacute;P TĂNG CHỨC NĂNG GIẢI ĐỘC CỦA GAN, GI&Uacute;P GIẢM NGỘ ĐỘC DO ĐỘC TỐ CỦA NẤM MỐC</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Sorbitol, Vitamin B1, B2, B6 v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Tăng cường chức năng giải độc, tiết mật, ngừa chứng kh&oacute; ti&ecirc;u, t&aacute;o b&oacute;n, ph&acirc;n sống. K&iacute;ch th&iacute;ch th&egrave;m ăn, tổng hợp protein gi&uacute;p gia s&uacute;c, gia cầm mau lớn, đẻ nhiều</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, ngan, ngỗng, thỏ, c&uacute;t, bồ c&acirc;u</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/1l nước hoặc 1kg/300-400kg thức ăn</p>\r\n\r\n<p><strong>Heo, d&ecirc;, cừu, b&ecirc;, ngh&eacute;&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/2l nước hoặc 1.5kg/1 tấn thức ăn</p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;, ngựa</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;:<strong>&nbsp;1g/5l nước hoặc 1kg/1 tấn thức ăn</strong></p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:19:13', NULL),
(19, 7, 'SORBITOL KHOÁNG NƯỚC', 'sorbitol-khoang-nuoc', 'SORBITOL KHOÁNG NƯỚC – GIẢI ĐỘC, MÁT GAN, TIÊU HOÁ TỐT, KÍCH THÍCH THÈM ĂN, TĂNG CƯỜNG HỆ MIỄN DỊCH', 0.00, NULL, '<p><strong>THUỐC UỐNG DẠNG LỎNG</strong></p>\r\n\r\n<p><strong>SORBITOL KHO&Aacute;NG NƯỚC &ndash; GIẢI ĐỘC, M&Aacute;T GAN, TI&Ecirc;U HO&Aacute; TỐT, K&Iacute;CH TH&Iacute;CH TH&Egrave;M ĂN, TĂNG CƯỜNG HỆ MIỄN DỊCH</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Sorbitol, Natri, Cu v&agrave; một số th&agrave;nh phần bổ sung kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;</p>\r\n\r\n<p>Tăng cường chức năng giải độc, tiết mật ở gia s&uacute;c, gia cầm, ph&ograve;ng ngừa chứng kh&oacute; ti&ecirc;u, t&aacute;o b&oacute;n, ph&acirc;n sống, ngộ độc thức ăn</p>\r\n\r\n<p>K&iacute;ch th&iacute;ch th&egrave;m ăn, n&acirc;ng cao hiệu quả sử dụng thức ăn, tổng hợp protein cho gia s&uacute;c, gia cầm mau lớn, đẻ nhiều</p>\r\n\r\n<p>T&aacute;i tạo tế b&agrave;o gan, phục hồi sức khoẻ v&agrave; tăng cường hệ miễn dịch ở gia s&uacute;c, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;, d&ecirc;, cừu, heo</strong>&nbsp; &nbsp; &nbsp; :1ml/10-15kg thể trọng</p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;:<strong>1ml/2l nước</strong></p>\r\n\r\n<p>Ho&agrave; nước cho uống hoặc trộn với thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:19:43', NULL),
(20, 7, 'SORBITOL-VIT', 'sorbitol-vit', 'SORBITOL-VIT – GIÚP TĂNG CƯỜNG KHẢ NĂNG CHỐNG CHỊU BỆNH TẬT CHO GIA SÚC, GIA CẦM. GIÚP TĂNG CƯỜNG SỨC ĐỀ KHÁNG, TĂNG CƯỜNG HẤP THỤ THỨC ĂN, TIÊU HOÁ TỐT', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>SORBITOL-VIT &ndash; GI&Uacute;P TĂNG CƯỜNG KHẢ NĂNG CHỐNG CHỊU BỆNH TẬT CHO GIA S&Uacute;C, GIA CẦM. GI&Uacute;P TĂNG CƯỜNG SỨC ĐỀ KH&Aacute;NG, TĂNG CƯỜNG HẤP THỤ THỨC ĂN, TI&Ecirc;U HO&Aacute; TỐT</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Sorbitol, Vitamin B1, B2, B6 v&agrave; một số th&agrave;nh phần bổ sung kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Tăng cường chức năng giải độc, tiết mật ở gia s&uacute;c, gia cầm, ph&ograve;ng ngừa chứng kh&oacute; ti&ecirc;u, t&aacute;o b&oacute;n, ph&acirc;n sống, ngộ độc thức ăn</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, ngan, ngỗng, thỏ, c&uacute;t, bồ c&acirc;u</strong>&nbsp; &nbsp; &nbsp; &nbsp; : 1g/1l nước uống hoặc 1kg/300-400kg thức ăn</p>\r\n\r\n<p><strong>Heo, d&ecirc;, cừu, b&ecirc;, ngh&eacute;&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1g/2l nước uống hoặc 1.5kg/1 tấn thức ăn.&nbsp;</p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;, ngựa</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;:<strong>&nbsp;1g/5l nước uống hoặc 1kg/1 tấn thức ăn</strong></p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:20:09', NULL),
(21, 7, 'TĂNG KHUNG XƯƠNG POLYVET', 'tang-khung-xuong-polyvet', 'DINH DƯỠNG DẠNG BỘT\r\nPOLYVET – CHỐNG CÒI, VỖ BÉO, MƯỢN LÔNG, ĐẸP MÃ', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>POLYVET &ndash; CHỐNG C&Ograve;I, VỖ B&Eacute;O, MƯỢN L&Ocirc;NG, ĐẸP M&Atilde;</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Vitamin C, B1, B2, B6, D3, Kẽm, L-Lysine, Canxi v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;N&acirc;ng cao sức đề kh&aacute;ng v&agrave; tăng cường hệ miễn dịch. Ph&ograve;ng bại liệt, c&ograve;i xương, mềm xương, xốp xương, khớp sưng to, gia cầm chậm lớn, ch&acirc;n kh&ocirc;, nứt m&oacute;ng. K&iacute;ch th&iacute;ch mọc l&ocirc;ng, chống cắn mổ</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, chim, c&uacute;t, ngan, ngỗng</strong>&nbsp; &nbsp; &nbsp; &nbsp; : 100g/100l nước hoặc 100g/100-150kg thức ăn</p>\r\n\r\n<p><strong>Lợn, tr&acirc;u, b&ograve;, d&ecirc;, cừu&nbsp;</strong>&nbsp;&nbsp; &nbsp;: 100g/100-200kg thức ăn</p>\r\n\r\n<p>Ho&agrave; với nước hoặc trộn với thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:23:58', NULL),
(22, 7, 'TĂNG LỰC BUTAMIN', 'tang-luc-butamin', 'DUNG DỊCH TIÊM\r\nBUTAMIN – TRỢ LỰC CẤP TỐC, TĂNG CƯỜNG TRAO ĐỔI CHẤT, PHỤC HỒI SỨC KHOẺ NHANH', 0.00, NULL, '<p><strong>DUNG DỊCH TI&Ecirc;M</strong></p>\r\n\r\n<p><strong>BUTAMIN &ndash; TRỢ LỰC CẤP TỐC, TĂNG CƯỜNG TRAO ĐỔI CHẤT, PHỤC HỒI SỨC KHOẺ NHANH</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Butaphosphan, Folic Acid, Vitamin B12 &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị rối loạn chguyển ho&aacute; do trao đỏi chất, dinh dưỡng k&eacute;m, chậm ph&aacute;t triển, tăng cường sức đề kh&aacute;ng tr&ecirc;n tr&acirc;u, b&ograve;, gnựa, cừu, lợn v&agrave; ch&oacute;</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1ml/25-30kg thể trọng/ng&agrave;y</p>\r\n\r\n<p><strong>Lợn, cừu, d&ecirc;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1ml/10-20kg thể trọng/ng&agrave;y</p>\r\n\r\n<p><strong>Gia cầm&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1ml/3-5kg thể trọng/ng&agrave;y</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:24:36', NULL),
(23, 7, 'W-PHAGE', 'w-phage', 'DẠNG LỎNG – SẢN PHẨM THUỘC BỘ W-PHAGE\r\nW-PHAGE – BẮT KHUẨN E.COLI, SALMONELLA, CLOSTRIDIUM', 0.00, NULL, '<p><strong>DẠNG LỎNG &ndash; SẢN PHẨM THUỘC&nbsp;<a href=\"https://winpharma.com.vn/product/bo-w-phage/\">BỘ W-PHAGE</a></strong></p>\r\n\r\n<p><strong>W-PHAGE &ndash; BẮT KHUẨN E.COLI, SALMONELLA, CLOSTRIDIUM</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:&nbsp;</strong>&nbsp;Bacillus spp&nbsp; &amp; Th&agrave;nh phần bổ sung 5 chủng thực khuẩnh thể (bacteriophase) đặc hiệu.</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG</strong>:&nbsp;</p>\r\n\r\n<p>Th&acirc;m nhập v&agrave; ăn hết c&aacute;c vi khuẩn l&agrave; E.coli, Salmonella, Clostridium,&hellip; đường ruột</p>\r\n\r\n<p>Tăng sức khoẻ đường ruột, sản sinh vi khuẩn c&oacute; lợi</p>\r\n\r\n<p>Tăng hấp thu thức ăn triệt để, giảm FCR</p>\r\n\r\n<p>Sử dụng ở tất cả c&aacute;c giai đoạn của vật nu&ocirc;i</p>\r\n\r\n<p><strong>LIỀU D&Ugrave;NG V&Agrave; C&Aacute;CH D&Ugrave;NG</strong>:</p>\r\n\r\n<p>Lắc đều trước khi sử dụng.</p>\r\n\r\n<p>Ph&ograve;ng bệnh: 1ml pha với 2 l&iacute;t nước</p>\r\n\r\n<p>Vật nu&ocirc;i non: D&ugrave;ng li&ecirc;n tục 7 ng&agrave;y, sau 15 ng&agrave;y sử dụng li&ecirc;n tục 2 ng&agrave;y/lần</p>\r\n\r\n<p>G&agrave; đẻ lần đầu: D&ugrave;ng li&ecirc;n tục 7 ng&agrave;y, sau đ&oacute; định kỳ 2 lần/tuần</p>\r\n\r\n<p>Heo: D&ugrave;ng li&ecirc;n tục 5 ng&agrave;y, sau đ&oacute; định kỳ 1 lần/1 tuần</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh s&aacute;ng trực tiếp, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n\r\n<p><strong>LƯU &Yacute;</strong>: Cần bổ sung phage thường xuy&ecirc;n đ&uacute;ng theo định kỳ.</p>\r\n', 0, '2025-11-12 15:25:14', NULL),
(24, 7, 'WIN MAX ORAL', 'win-max-oral', 'WIN MAX ORAL – BỔ GAN GIẢN ĐỘC, TĂNG CƯỜNG SỨC ĐỀ  KHÁNG, KÍCH THÍCH TIÊU HOÁ GIÚP VẬT NUÔI MAU LỚN', 0.00, NULL, '<p><strong>THUỐC UỐNG DẠNG LỎNG</strong></p>\r\n\r\n<p><strong>WIN MAX ORAL &ndash; BỔ GAN GIẢN ĐỘC,&nbsp;</strong><strong>TĂNG CƯỜNG SỨC ĐỀ&nbsp; KH&Aacute;NG,&nbsp;</strong><strong>K&Iacute;CH TH&Iacute;CH TI&Ecirc;U HO&Aacute; GI&Uacute;P VẬT NU&Ocirc;I MAU LỚN</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong></p>\r\n\r\n<p>Sorbitol (Min: 180.000mg), Lysine (Min: 1.400mg), Bổ sung vitamin, kho&aacute;ng v&agrave; probiotic, Bổ sung enzyme, Th&agrave;nh phần thảo dược</p>\r\n\r\n<p><em>Kh&ocirc;ng chứa kh&aacute;ng sinh, hoocmon v&agrave; c&aacute;c chất độc hại.</em></p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Bổ sung axit amin bồi bổ cơ thể, tăng cường chuyển ho&aacute;, n&acirc;ng cao sức đề kh&aacute;ng. Hỗ trợ chức năng gan thận, k&iacute;ch th&iacute;ch ti&ecirc;u ho&aacute;, giải độc tố gan, thận bị nhiễm độc tố khi d&ugrave;ng kh&aacute;ng sinh l&acirc;u ng&agrave;y. K&iacute;ch th&iacute;ch ti&ecirc;u ho&aacute; hạn chế mổ l&ocirc;ng, cắn đu&ocirc;i, rụng l&ocirc;ng.</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1ml/1 l&iacute;t nước uống/ng&agrave;y</p>\r\n\r\n<p><strong>Heo, tr&acirc;u, b&ograve;, d&ecirc;, cừu&nbsp;&nbsp;</strong>&nbsp;&nbsp; &nbsp;: 1ml/7-10kg thể trọng/ng&agrave;y&nbsp;</p>\r\n\r\n<p>Ho&agrave; nước cho uống hoặc trộn với thức ăn. Sử dụng li&ecirc;n tục trong suốt thời gian nu&ocirc;i gia s&uacute;c gia cầm.</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh s&aacute;ng trực tiếp, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n\r\n<p><strong>HSD</strong>: 2 năm kể từ ng&agrave;y sản xuất.</p>\r\n', 0, '2025-11-12 15:25:42', NULL),
(25, 7, 'WIN-SẮT B12', 'win-sat-b12', 'WIN-SẮT B12 – TRỊ VÀ PHÒNG NGỪA THIẾU MÁU DO THIẾU SẮT TRÊN BÊ VÀ LỢN CON', 0.00, NULL, '<p><strong>DUNG DỊCH TI&Ecirc;M</strong></p>\r\n\r\n<p><strong>WIN-SẮT B12 &ndash; TRỊ V&Agrave; PH&Ograve;NG NGỪA THIẾU M&Aacute;U DO THIẾU SẮT TR&Ecirc;N B&Ecirc; V&Agrave; LỢN CON</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Fer.dextran, Vitamin B12 &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị v&agrave; ph&ograve;ng thiếu m&aacute;u do thiếu sắt tr&ecirc;n b&ecirc; v&agrave; lợn con</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Lợn con</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: Ph&ograve;ng bệnh: 1-2ml/con; Chữa bệnh: 2-3ml/con</p>\r\n\r\n<p><strong>Lợn n&aacute;i</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 3ml/con</p>\r\n\r\n<p><strong>D&ecirc; con&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 2-3ml/con</p>\r\n\r\n<p><strong>B&ecirc;, ngh&eacute;, ngựa</strong>&nbsp; &nbsp;: 8-12ml/con</p>\r\n\r\n<p><strong>Ch&oacute;, m&egrave;o</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 0.5-2ml/con</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:26:16', NULL),
(26, 7, 'XỬ LÝ CÒI CỌC MULTIVITA-AMIN', 'xu-ly-coi-coc-multivita-amin', 'MULTIVITA-AMIN – CUNG CẤP VITAMIN VÀ AXIT AMIN GIÚP CHO GIA SÚC, GIA CẦM THÈM ĂN, ĂN NHIỀU, TIÊU HOÁ TỐT, TĂNG TRỌNG NHANH, GIÚP XƯƠNG CHẮC KHOẺ, VỎ TRỨNG DÀY', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>MULTIVITA-AMIN &ndash; CUNG CẤP VITAMIN V&Agrave; AXIT AMIN GI&Uacute;P CHO GIA S&Uacute;C, GIA CẦM TH&Egrave;M ĂN, ĂN NHIỀU, TI&Ecirc;U HO&Aacute; TỐT, TĂNG TRỌNG NHANH, GI&Uacute;P XƯƠNG CHẮC KHOẺ, VỎ TRỨNG D&Agrave;Y</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Vitamin B1, C, Lysine, Vitamin B6, Methionine v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Tăng sức đề kh&aacute;ng, chống c&ograve;i cọc, chậm lớn, k&iacute;ch th&iacute;ch t&iacute;nh th&egrave;m ăn, tăng trọng nhanh, mau phục hồi sức khoẻ sau khi bệnh</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, chim, ngan, ngỗng</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/1-2l nước hoặc 100g/50kg thức ăn</p>\r\n\r\n<p><strong>Lợn, tr&acirc;u, b&ograve;</strong>&nbsp; &nbsp; : 100g/80-100l nước hoặc 100g/100-150kg thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:26:50', NULL),
(27, 8, 'AMMOXCLANIC', 'ammoxclanic', 'AMMOXCLANIC – ĐẶC TRỊ VIÊM DA, NHIỄM TRÙNG ĐƯỜNG HÔ HẤP, NHIỄM TRÙNG ĐƯỜNG TIẾT NIỆU, NHIỄM TRÙNG DA VÀ MÔ MỀM', 0.00, NULL, '<p><strong>HỖN DỊCH TI&Ecirc;M</strong></p>\r\n\r\n<p><strong>AMMOXCLANIC &ndash; ĐẶC TRỊ VI&Ecirc;M DA, NHIỄM TR&Ugrave;NG ĐƯỜNG H&Ocirc; HẤP, NHIỄM TR&Ugrave;NG ĐƯỜNG TIẾT NIỆU, NHIỄM TR&Ugrave;NG DA V&Agrave; M&Ocirc; MỀM</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Amoxicilin, Clavulanic Acid &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị vi&ecirc;m da, nhiễm tr&ugrave;ng đường h&ocirc; hấp, tiết niệu, nhiễm tr&ugrave;ng da v&agrave; m&ocirc; mềm tr&ecirc;n tr&acirc;u, b&ograve;, lợn, gia cầm, ch&oacute;</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Lợn, gia s&uacute;c</strong>&nbsp; &nbsp; : 1ml/20kg thể trọng</p>\r\n\r\n<p><strong>Ch&oacute; m&egrave;o</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1ml/5-7kg thể trọng</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:28:00', NULL),
(28, 8, 'AMMOXGENX', 'ammoxgenx', 'AMMOXGENX – CHỮA NHIỄM TRÙNG ĐƯỜNG TIÊU HOÁ, HÔ HẤP VÀ NHIỄM TRÙNG NỘI TUỶ, VIÊM RUỘT DO VI KHUẨN', 0.00, NULL, '<p><strong>HỖN DỊCH TI&Ecirc;M</strong></p>\r\n\r\n<p><strong>AMMOXGENX &ndash; CHỮA NHIỄM TR&Ugrave;NG ĐƯỜNG TI&Ecirc;U HO&Aacute;, H&Ocirc; HẤP V&Agrave; NHIỄM TR&Ugrave;NG NỘI TUỶ, VI&Ecirc;M RUỘT DO VI KHUẨN</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Amoxicilin trihydrat, Gentamycin &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm tr&ugrave;ng đường ti&ecirc;u ho&aacute;, h&ocirc; hấp v&agrave; nhiễm tr&ugrave;ng nội tuỷ, vi&ecirc;m v&uacute;, vi&ecirc;m tử cung, &aacute;p xe da tr&ecirc;n tr&acirc;u, b&ograve;, lợn</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Gia s&uacute;c lớn</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1ml/10kg thể trọng</p>\r\n\r\n<p><strong>Gia s&uacute;c nhỏ</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1ml/7kg thể trọng</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:28:30', NULL);
INSERT INTO `products` (`id`, `category_id`, `title`, `slug`, `short_description`, `price`, `promo_price`, `description`, `display_order`, `created_at`, `updated_at`) VALUES
(29, 8, 'AMOCICOL 200W', 'amocicol-200w', 'AMOCICOL 200W – ĐẶC TRỊ TIÊU CHẢY, HEN, SƯNG PHÙ ĐẦU, VIÊM RUỘT HOẠI TỬ, TỤ HUYẾT TRÙNG, E.COLI, BẠCH LỴ, VIÊM TIẾT NIỆU', 0.00, NULL, '<p><strong>THUỐC BỘT</strong></p>\r\n\r\n<p><strong>AMOCICOL 200W &ndash; ĐẶC TRỊ TI&Ecirc;U CHẢY, HEN, SƯNG PH&Ugrave; ĐẦU, VI&Ecirc;M RUỘT HOẠI TỬ, TỤ HUYẾT TR&Ugrave;NG, E.COLI, BẠCH LỴ, VI&Ecirc;M TIẾT NIỆU</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:&nbsp;</strong>&nbsp;Amoxicilin trihydrate, Colistin sulfate &amp; T&aacute; dược độc quyền.</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG</strong>:&nbsp;Trị nhiễm tr&ugrave;ng đường h&ocirc; hấp, ti&ecirc;u ho&aacute;, tiết niệu tr&ecirc;n b&ecirc;, ngh&eacute;, d&ecirc;, gia cầm, cừu, lợn.</p>\r\n\r\n<p><strong>CHỈ ĐỊNH CỦA BSTY</strong>:</p>\r\n\r\n<p>Heo v&agrave; gia s&uacute;c kh&aacute;c: Ph&ograve;ng v&agrave; trị bệnh vi&ecirc;m phổi địa phương tr&ecirc;n heo (SEP), tụ huyết tr&ugrave;ng, vi&ecirc;m ruột ti&ecirc;u chảy do E.coli.</p>\r\n\r\n<p>G&agrave;, vịt, c&uacute;t: Ph&ograve;ng v&agrave; trị bệnh h&ocirc; hấp m&atilde;n t&iacute;nh CRD v&agrave; c&aacute;c biến chứng của bệnh (CCRD), vi&ecirc;m khớ, vi&ecirc;m xoang mũi, ti&ecirc;u chảy ph&acirc;n trắng, ph&acirc;n xanh</p>\r\n\r\n<p><strong>C&Aacute;CH D&Ugrave;NG V&Agrave; LIỀU LƯỢNG</strong>:</p>\r\n\r\n<p>Heo v&agrave; gia s&uacute;c&nbsp; &nbsp; &nbsp; : 1g/20kg TT/ng&agrave;y</p>\r\n\r\n<p>Gia cầm&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1g/14-20kg TT/ng&agrave;y</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH</strong>: Kh&ocirc;ng d&ugrave;ng cho vật nu&ocirc;i mẫn cảm với th&agrave;nh phần của thuốc</p>\r\n\r\n<p><strong>THỜI GIAN NGƯNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh s&aacute;ng trực tiếp, nhiệt độ m&aacute;t dưới 30&deg;C.</p>\r\n', 0, '2025-11-12 15:29:00', NULL),
(30, 8, 'AMOXCLA POWDER', 'amoxcla-powder', 'AMOXCLA POWDER – ĐẶC TRỊ VIÊM PHỔI, CRD, TIÊU CHẢY PHÂN XANH, PHÂN TRẮNG', 0.00, NULL, '<p><strong>THUỐC BỘT</strong></p>\r\n\r\n<p><strong>AMOXCLA POWDER &ndash; ĐẶC TRỊ VI&Ecirc;M PHỔI, CRD, TI&Ecirc;U CHẢY PH&Acirc;N XANH, PH&Acirc;N TRẮNG</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Amoxycillin trihydrat, Clavulanic Acid &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm tr&ugrave;ng da v&agrave; m&ocirc; mềm tr&ecirc;n lợn, gia cầm</p>\r\n\r\n<p><strong>CHỈ ĐỊNH CỦA BSTY</strong>:</p>\r\n\r\n<p><strong>Gia cầm</strong>: Đặc trị E.coli, thương h&agrave;n, CRD, E.coli gh&eacute;p, vi&ecirc;m ruột hoại tử, ti&ecirc;u chảy ph&acirc;n trắng, thương h&agrave;n, bạch lỵ, vi&ecirc;m phế quản, tụ huyết tr&ugrave;ng</p>\r\n\r\n<p><strong>Gia s&uacute;c</strong>: trị vi&ecirc;m phổi, vi&ecirc;m teo mũi truyền nhiễm, vi&ecirc;m xoang, vi&ecirc;m khớp, vi&ecirc;m rốn, vi&ecirc;m v&uacute;, vi&ecirc;m tử cung, E.coli, thương h&agrave;n</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p>Liều điều trị:</p>\r\n\r\n<p><strong>Lợn</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1kg/0.6-0.8 tấn thức ăn, d&ugrave;ng li&ecirc;n tục 3-5 ng&agrave;y</p>\r\n\r\n<p><strong>Gia cầm&nbsp;</strong>&nbsp; &nbsp; &nbsp;: 1kg/1 tấn thức ăn, d&ugrave;ng li&ecirc;n tục 3-5 ng&agrave;y</p>\r\n\r\n<p>Liều ph&ograve;ng bằng 1/2 li&ecirc;u điều trị</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:29:37', NULL),
(31, 8, 'AMOXCOL 500WS', 'amoxcol-500ws', 'AMOXCOL 500WS – ĐẶC TRỊ VIÊM RUỘT, THƯƠNG HÀN, TỤ HUYẾT TRÙNG', 0.00, NULL, '<p><strong>THUỐC BỘT</strong></p>\r\n\r\n<p><strong>AMOXCOL 500WS &ndash; ĐẶC TRỊ VI&Ecirc;M RUỘT, THƯƠNG H&Agrave;N, TỤ HUYẾT TR&Ugrave;NG</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Amoxicilin trihydrat, Colistin sulfat &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm tr&ugrave;ng đường h&ocirc; hấp, ti&ecirc;u ho&aacute;, tiết liệu tr&ecirc;n b&ecirc;, ngh&eacute;, d&ecirc;, gia cầm, cừu, lợn</p>\r\n\r\n<p><strong>CHỈ ĐỊNH CỦA BSTY</strong>:</p>\r\n\r\n<p><strong>Heo v&agrave; gia s&uacute;c kh&aacute;</strong>c: Ph&ograve;ng v&agrave; trị bệnh vi&ecirc;m phổi địa phương tr&ecirc;n heo (SEP), tụ huyết tr&ugrave;ng, vi&ecirc;m ruột ti&ecirc;u chảy do E.coli</p>\r\n\r\n<p><strong>G&agrave;, vịt, c&uacute;t</strong>: Ph&ograve;ng v&agrave; trị bệnh h&ocirc; hấp m&atilde;n t&iacute;nh CRD v&agrave; c&aacute;c biến chứng của bệnh CCRD, vi&ecirc;m khớp, vi&ecirc;m xoang mũi, ti&ecirc;u chảy ph&acirc;n trắng, ph&acirc;n xanh.</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1g/30kg thể trọng/ng&agrave;y</p>\r\n\r\n<p><strong>Heo v&agrave; gia s&uacute;c</strong>&nbsp; &nbsp; : 1g/40kg thể trọng/ng&agrave;y</p>\r\n\r\n<p>Pha nước uống hoặc trộn với thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: Thịt: 8 ng&agrave;y; Sữa: 2 ng&agrave;y</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:30:09', NULL),
(32, 8, 'AMOXCOLIS 64', 'amoxcolis-64', 'AMOXCOLIS 64 – ĐẶC TRỊ VIÊM RUỘT HOẠI TỬ, THƯƠNG HÀN, TỤ HUYẾT TRÙNG, CORYZA, HEN GHÉP E.COLI', 0.00, NULL, '<p><strong>THUỐC BỘT</strong></p>\r\n\r\n<p><strong>AMOXCOLIS 64 &ndash; ĐẶC TRỊ VI&Ecirc;M RUỘT HOẠI TỬ, THƯƠNG H&Agrave;N, TỤ HUYẾT TR&Ugrave;NG, CORYZA, HEN GH&Eacute;P E.COLI</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Amoxicillin trihydrat, Colistin sulfat &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm tr&ugrave;ng h&ocirc; hấp, ti&ecirc;u ho&aacute; tr&ecirc;n lợn, gia cầm</p>\r\n\r\n<p><strong>CHỈ ĐỊNH CỦA BSTY</strong>:</p>\r\n\r\n<p><strong>Gia cầm</strong>: Đặc trị vi&ecirc;m ruột hoại tử, thương h&agrave;n, kh&ograve; kh&egrave;, s&atilde; c&aacute;nh, ph&acirc;n xanh, ph&acirc;n trắng, ph&acirc;n v&agrave;ng, ph&acirc;n nhớt c&oacute; bọt kh&iacute;, hen khẹc, ng&aacute;p, vẩy mỏ, tụ huyết tr&ugrave;ng, E.coli bại huyết, E.coli k&eacute;o m&agrave;ng, Coryza sưng ph&ugrave; mặt, cầu tr&ugrave;ng gh&eacute;p vi&ecirc;m ruột</p>\r\n\r\n<p><strong>Gia s&uacute;c</strong>: Đặc trị vi&ecirc;m ruột hoại tử, li&ecirc;n cầu khuẩn, ph&acirc;n b&ugrave;n, x&aacute;m c&oacute; bọt kh&iacute;, thương h&agrave;n, tụ huyết tr&ugrave;ng, đ&oacute;ng dấu lợn, vi&ecirc;m da.</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp;: 1g/7.5l nước hoặc 1g/35-40kg thể trọng/ng&agrave;y</p>\r\n\r\n<p><strong>Gia s&uacute;c</strong>&nbsp; &nbsp; : 1g/50-60kg thể trọng/ng&agrave;y</p>\r\n\r\n<p>Trộn v&agrave;o thức ăn hoặc pha nước uống, d&ugrave;ng li&ecirc;n tục 3-5 ng&agrave;y</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:30:39', NULL),
(33, 9, 'AMOXI 500W', 'amoxi-500w', 'AMOXI 500W – VIÊM RUỘT, THƯƠNG HÀN, TỤ HUYẾT TRÙNG, TIÊU CHẢY, PHÂN TRẮNG, PHÂN VÀNG, VIÊM PHỔI, PHÓ THƯƠNG HÀN', 0.00, NULL, '<p><strong>THUỐC BỘT</strong></p>\r\n\r\n<p><strong>AMOXI 500W &ndash; VI&Ecirc;M RUỘT, THƯƠNG H&Agrave;N, TỤ HUYẾT TR&Ugrave;NG, TI&Ecirc;U CHẢY, PH&Acirc;N TRẮNG, PH&Acirc;N V&Agrave;NG, VI&Ecirc;M PHỔI, PH&Oacute; THƯƠNG H&Agrave;N</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Amoxycillin trihydrat &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm khuẩn h&ocirc; hấp, ti&ecirc;u ho&aacute; tr&ecirc;n lợn, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp;: 1g/25kg thể trọng/ng&agrave;y</p>\r\n\r\n<p><strong>Gia s&uacute;c&nbsp;</strong>&nbsp; &nbsp; &nbsp;: 1g/30-40kg thể trọng/ng&agrave;y</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:35:32', NULL),
(34, 9, 'ANPHA NEW', 'anpha-new', 'ANPHA NEW – ĐẶC TRỊ VIÊM PHỔI, ĐIỀU TRỊ BỘI NHIỄM DO BỆNH TAI XANH, LMLM', 0.00, NULL, '<p><strong>DUNG DỊCH TI&Ecirc;M</strong></p>\r\n\r\n<p><strong>ANPHA NEW &ndash; ĐẶC TRỊ VI&Ecirc;M PHỔI, ĐIỀU TRỊ BỘI NHIỄM DO BỆNH TAI XANH, LMLM</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Azithromycin &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm khuẩn đường h&ocirc; hấp, ti&ecirc;u ho&aacute;, sinh dục, nhiễm tr&ugrave;ng da v&agrave; c&aacute;c m&ocirc; mềm tr&ecirc;n tr&acirc;u, b&ograve;, ngựa, d&ecirc;, cừu v&agrave; lợn</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong>Ti&ecirc;m bắp thịt c&aacute;ch nhau 48h. Nếu bệnh nặng ng&agrave;y ti&ecirc;m 1 lần với liều 1ml/15-20kg thể trọng</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGƯNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:36:10', NULL),
(35, 9, 'CEFANEW @', 'cefanew', 'CEFANEW @ – VIÊM VÚ, VIÊM TỬ CUNG, VIÊM ĐƯỜNG TIẾT NIỆU, VIÊM DA, VIÊM KHỚP, THỐI MÓNG, LỞ MỒM LONG MÓNG, SỐT KHÔNG RÕ NGUYÊN NHÂN', 0.00, NULL, '<p><strong>HỖN DỊCH TI&Ecirc;M</strong></p>\r\n\r\n<p><strong>CEFANEW @ &ndash; VI&Ecirc;M V&Uacute;, VI&Ecirc;M TỬ CUNG, VI&Ecirc;M ĐƯỜNG TIẾT NIỆU, VI&Ecirc;M DA, VI&Ecirc;M KHỚP, THỐI M&Oacute;NG, LỞ MỒM LONG M&Oacute;NG, SỐT KH&Ocirc;NG R&Otilde; NGUY&Ecirc;N NH&Acirc;N</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Ceftiofur &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm tr&ugrave;ng đường h&ocirc; hấp, vi&ecirc;m tử cung cấp tr&ecirc;n tr&acirc;u, b&ograve;, lợn</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Lợn</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1ml/10-15kg thể trọng/ng&agrave;y. D&ugrave;ng 3 ng&agrave;y</p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;</strong>&nbsp; &nbsp; : 1-2ml/50kg thể trọng/ng&agrave;y. D&ugrave;ng 3-5 ng&agrave;y</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:36:40', NULL),
(36, 9, 'CEFKE 515 @ LA', 'cefke-515-la', 'CEFKE 515 – VIÊM VÚ, VIÊM TỬ CUNG, VIÊM ĐƯỜNG TIẾT NIỆU, VIÊM DA, VIÊM KHỚP, THỐI MÓNG, LỞ MỒM LONG MÓNG, SỐT KHÔNG RÕ NGUYÊN NHÂN', 0.00, NULL, '<p><strong>HỖN DỊCH TI&Ecirc;M</strong></p>\r\n\r\n<p><strong>CEFKE 515 &ndash; VI&Ecirc;M V&Uacute;, VI&Ecirc;M TỬ CUNG, VI&Ecirc;M ĐƯỜNG TIẾT NIỆU, VI&Ecirc;M DA, VI&Ecirc;M KHỚP, THỐI M&Oacute;NG, LỞ MỒM LONG M&Oacute;NG, SỐT KH&Ocirc;NG R&Otilde; NGUY&Ecirc;N NH&Acirc;N</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Ceftiofur, Ketoprofen &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm khuẩn h&ocirc; hấp tr&ecirc;n b&ograve; (BRD)</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Lợn</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1ml/10-15kg thể trọng/ng&agrave;y. D&ugrave;ng 3 ng&agrave;y</p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;</strong>&nbsp; &nbsp; : 1-2ml/50kg thể trọng/ng&agrave;y. D&ugrave;ng 3-5 ng&agrave;y</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:37:11', NULL),
(37, 9, 'CEFQUI 25LA', 'cefqui-25la', 'CEFQUI 25LA – ĐẶC TRỊ CÁC BỆNH NHIỄM KHUẨN, VIÊM DA, HOẠI TỬ DO NHIỄM TRÙNG VÀ BỆNH HOẠI TỬ', 0.00, NULL, '<p><strong>HỖN DỊCH TI&Ecirc;M</strong></p>\r\n\r\n<p><strong>CEFQUI 25LA &ndash; ĐẶC TRỊ C&Aacute;C BỆNH NHIỄM KHUẨN, VI&Ecirc;M DA, HOẠI TỬ DO NHIỄM TR&Ugrave;NG V&Agrave; BỆNH HOẠI TỬ</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Cefquinom &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm khuẩn đường h&ocirc; hấp, vi&ecirc;m da hoại tử, vi&ecirc;m v&uacute;, nhiễm tr&ugrave;ng huyết tr&acirc;u, b&ograve;, lợn, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Lợn, b&ecirc;, ngh&eacute;, d&ecirc;, ngựa con</strong>&nbsp; &nbsp; &nbsp;: 1ml/12.5kg thể trọng</p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;, ngựa</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1ml/25kg thể trọng</p>\r\n\r\n<p><strong>Ch&oacute; m&egrave;o&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1ml/8-10kg thể trọng</p>\r\n\r\n<p><strong>Gia cầm&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1ml/5-7kg thể trọng</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:37:41', NULL),
(38, 10, 'KÍCH ĐẺ WIN TOCIN', 'kich-de-win-tocin', 'WIN TOCIN – CHỮA LIỆT DẠ CON, TĂNG CƯỜNG CO TỬ CUNG, HỖ TRỢ ĐIỀU TRỊ VIÊM VÚ, VIÊM TỬ CUNG', 0.00, NULL, '<p><strong>DUNG DỊCH TI&Ecirc;M</strong></p>\r\n\r\n<p><strong>WIN TOCIN &ndash; CHỮA LIỆT DẠ CON, TĂNG CƯỜNG CO TỬ CUNG, HỖ TRỢ ĐIỀU TRỊ VI&Ecirc;M V&Uacute;, VI&Ecirc;M TỬ CUNG</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Oxytocin &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Chữa liệt dạ con, tăng cường co tử cung, th&uacute;c đẻ, tống nhau thai v&agrave; c&aacute;c dịch ứ ra nhanh, ph&ograve;ng băng huyết, chảy m&aacute;u dạ con khi đẻ, k&iacute;ch th&iacute;ch tiết sữa, hỗ trợ điều trị vi&ecirc;m v&uacute;, vi&ecirc;m tử cung tr&ecirc;n ngựa, b&ograve;, cừu, d&ecirc;, lợn, ch&oacute;, m&egrave;o</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;, ngựa</strong>&nbsp; : 2-5ml/con</p>\r\n\r\n<p><strong>Heo, d&ecirc;, cừu&nbsp;</strong>&nbsp;&nbsp; &nbsp;: 1-2ml/con</p>\r\n\r\n<p><strong>Ch&oacute;&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 0.2-1ml/con</p>\r\n\r\n<p><strong>M&egrave;o&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 0.2-0.5ml/con</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG THUỐC:</strong>&nbsp;7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:39:01', NULL),
(39, 10, 'KÍCH DỤC TỐ VITA-ADE', 'kich-duc-to-vita-ade', 'VITA-ADE – BỔ SUNG VITAMIN ADE, PHÒNG SẢY THAI, ĐẺ NON, TĂNG TỶ LỆ PHÔI, TỶ LỆ ẤP NỞ, TĂNG SẢN LƯỢNG TRỨNG', 0.00, NULL, '<p><strong>DINH DƯỠNG DẠNG BỘT</strong></p>\r\n\r\n<p><strong>VITA-ADE &ndash; BỔ SUNG VITAMIN ADE, PH&Ograve;NG SẢY THAI, ĐẺ NON, TĂNG TỶ LỆ PH&Ocirc;I, TỶ LỆ ẤP NỞ, TĂNG SẢN LƯỢNG TRỨNG</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Vitamin A, D3, E v&agrave; một số th&agrave;nh phần kh&aacute;c</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;</p>\r\n\r\n<p>Cung cấp vitamin ADE, ph&ograve;ng ngừa sảy thai, chết ph&ocirc;i, k&iacute;ch th&iacute;ch tăng trọng nhanh</p>\r\n\r\n<p>Tăng sức đề kh&aacute;ng, tăng cường hệ miễn dịch, chống stress, đăc biệt rất cần thiết đối với sự ph&aacute;t triển cơ quan sinh sản của vật nu&ocirc;i trong giai đoạn hậu bị v&agrave; sinh sản</p>\r\n\r\n<p>Tăng sản lượng trứng v&agrave; k&eacute;o d&agrave;i thời gian khai th&aacute;c trứng. Tăng chất lượng trứng, trứng to, khắc phục t&igrave;nh trạng vỏ mỏng, dễ vỡ</p>\r\n\r\n<p>L&ecirc;n giống nhanh, tăng tỷ lệ đậu thai ở gia s&uacute;c</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, ngan, ngỗng, chim, c&uacute;t</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/1-2l nước hoặc 2g/1-2kg thức ăn</p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;, d&ecirc;, cừu,heo&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>&nbsp; &nbsp;: 1g/8-10l nước hoặc 1g/2kg thức ăn</p>\r\n\r\n<p>Ho&agrave; nước cho uống hoặc trộn với thức ăn, sử dụng li&ecirc;n tục trong suốt thời gian nu&ocirc;i gia s&uacute;c, gia cầm</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:39:39', NULL),
(40, 12, 'ĐẶC TRỊ GIUN SÁN', 'dac-tri-giun-san', 'ĐẶC TRỊ GIUN SÁN – ĐẶC TRỊ CÁC LOẠI GIUN TRÒN KÝ SINH', 0.00, NULL, '<p><strong>THUỐC BỘT</strong></p>\r\n\r\n<p><strong>ĐẶC TRỊ GIUN S&Aacute;N &ndash; ĐẶC TRỊ C&Aacute;C LOẠI GIUN TR&Ograve;N K&Yacute; SINH</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Fenbendazole &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị k&yacute; sinh tr&ugrave;ng tr&ecirc;n tr&acirc;u, b&ograve;, b&ecirc;, ngh&eacute;, d&ecirc;, cừu, lợn, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/15-20kg thể trọng</p>\r\n\r\n<p><strong>Lợn, d&ecirc;, cừu&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1g/12-16kg thể trọng</p>\r\n\r\n<p><strong>Ch&oacute; m&egrave;o&nbsp; &nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/5-10kg thể trọng</p>\r\n\r\n<p><strong>G&agrave;, vịt, ngan, ngỗng, c&uacute;t&nbsp; &nbsp;</strong>: 1g/5-10kg thể trọng</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:41:05', NULL),
(41, 12, 'DICLACOC', 'diclacoc', 'DICLACOC – TRỊ CẦU TRÙNG TRÊN GIA SÚC, GIA CẦM', 0.00, NULL, '<p><strong>THUỐC HỖN DỊCH UỐNG</strong></p>\r\n\r\n<p><strong>DICLACOC &ndash; TRỊ CẦU TR&Ugrave;NG TR&Ecirc;N GIA S&Uacute;C, GIA CẦM</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Diclazuril &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị cầu tr&ugrave;ng tr&ecirc;n gia s&uacute;c, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Gia s&uacute;c</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1ml/5-10kg thể trọng/ng&agrave;y</p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;:<strong>&nbsp;1ml/0.5l nước uống</strong></p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng sử dụng cho vật nu&ocirc;i mẫn cảm với th&agrave;nh phần của thuốc</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG THUỐC:</strong>&nbsp;7 ng&agrave;y trước khi giết mổ.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh nắng trực tiếp, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:41:34', NULL),
(42, 12, 'LEVASOL', 'levasol', 'LEVASOL – PHÒNG VÀ TRỊ KÝ SINH TRÙNG ĐƯỜNG TIÊU HOÁ VÀ GIUN PHỔI TRÊN TRÂU, BÒ, BÊ, NGHÉ, DÊ, CỪU VÀ LỢN', 0.00, NULL, '<p><strong>DUNG DỊCH TI&Ecirc;M</strong></p>\r\n\r\n<p><strong>LEVASOL &ndash; PH&Ograve;NG V&Agrave; TRỊ K&Yacute; SINH TR&Ugrave;NG ĐƯỜNG TI&Ecirc;U HO&Aacute; V&Agrave; GIUN PHỔI TR&Ecirc;N TR&Acirc;U, B&Ograve;, B&Ecirc;, NGH&Eacute;, D&Ecirc;, CỪU V&Agrave; LỢN</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Levamisole &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Ph&ograve;ng v&agrave; trị l&yacute; sinh tr&ugrave;ng đường ti&ecirc;u ho&aacute; v&agrave; giun phổi tr&ecirc;n tr&acirc;u, b&ograve;, b&ecirc;, ngh&eacute;, d&ecirc;, cừu, lợn</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Tr&acirc;u, b&ograve;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1ml/20kg thể trọng</p>\r\n\r\n<p><strong>Lợn, d&ecirc;, cừu&nbsp;</strong>&nbsp;&nbsp; &nbsp;: 1ml/20kg thể trọng&nbsp;</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG THUỐC:</strong>&nbsp;7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:42:05', NULL),
(43, 12, 'W-SULFATRYL 408', 'w-sulfatryl-408', 'W-SULFATRYL 408 – ĐẶC TRỊ CẦU TRÙNG, KÝ SINH TRÙNG ĐƯỜNG MÁU, PHÂN SÁP, CẦU TRÙNG MÁU TƯƠI, VIÊM RUỘT TIÊU CHẢY, TỤ HUYẾT TRÙNG', 0.00, NULL, '<p><strong>THUỐC BỘT</strong></p>\r\n\r\n<p><strong>W-SULFATRYL 408 &ndash; ĐẶC TRỊ CẦU TR&Ugrave;NG, K&Yacute; SINH TR&Ugrave;NG ĐƯỜNG M&Aacute;U, PH&Acirc;N S&Aacute;P, CẦU TR&Ugrave;NG M&Aacute;U TƯƠI, VI&Ecirc;M RUỘT TI&Ecirc;U CHẢY, TỤ HUYẾT TR&Ugrave;NG</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Sulfamonomethoxin sodium, Trimethoprim &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị ti&ecirc;u chảy, nhiễm tr&ugrave;ng huyết, cầu tr&ugrave;ng, vi&ecirc;m đường h&ocirc; h&aacute;p, nhiễm tr&ugrave;ng t&uacute;i kh&iacute;, vi&ecirc;m đa khớp, bạch hầu v&agrave; nhiễm tr&ugrave;ng d&acirc;y rốn tr&ecirc;n b&ecirc;, ngh&eacute;, d&ecirc;, lợn, g&agrave; v&agrave; g&agrave; t&acirc;y</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/2-3l nước hoặc 1g/10-15kg thể trọng</p>\r\n\r\n<p><strong>B&ecirc;, ngh&eacute;, d&ecirc;, cừu, lợn</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/20kg thể trọng</p>\r\n\r\n<p>Pha nước hoặc trộn với thức ăn</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:47:18', NULL),
(44, 12, 'W-SULFA GOLD', 'w-sulfa-gold', 'W-SULFA GOLD – ĐẶC TRỊ KÍ SINH TRUNG ĐƯỜNG MÁU; CẦU TRÙNG RUỘT NON, MANH TRÀNG; VIÊM RUỘT HOẠI TỬ, TỤ HUYẾT TRÙNG', 0.00, NULL, '<p><strong>THUỐC UỐNG</strong></p>\r\n\r\n<p><strong>W-SULFA GOLD &ndash; ĐẶC TRỊ K&Iacute; SINH TRUNG ĐƯỜNG M&Aacute;U; CẦU TR&Ugrave;NG RUỘT NON, MANH TR&Agrave;NG; VI&Ecirc;M RUỘT HOẠI TỬ, TỤ HUYẾT TR&Ugrave;NG</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Sulfamonomethoxin, Trimethopruim &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm tr&ugrave;ng đường h&ocirc; hấp, ti&ecirc;u ho&aacute;, niệu sinh dục, vi&ecirc;m tắc tĩnh mạch, vi&ecirc;m m&oacute;ng, vi&ecirc;m kết mạc tr&ecirc;n b&ecirc;, ngh&eacute;, lợn, gia cầm</p>\r\n\r\n<p><strong>CHỈ ĐỊNH CỦA BSTY</strong>: Đặc trị k&yacute; sinh tr&ugrave;ng đường m&aacute;u, bệnh đầu đen, ch&agrave;m hậu m&ocirc;n, cầu tr&ugrave;ng ruột non, cầu tr&ugrave;ng manh tr&agrave;ng, cầu tr&ugrave;ng gh&eacute;p E.coli, ti&ecirc;u chảy ra m&aacute;u, ph&acirc;n s&aacute;p, ph&acirc;n socola, vi&ecirc;m ruột hoại tử, tụ huyết tr&ugrave;ng, bạch lỵ, kh&ocirc; ch&acirc;n ở g&agrave;, vịt, ngan, c&uacute;t</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>G&agrave;, vịt, ngan, c&uacute;t&nbsp;</strong>&nbsp; &nbsp; &nbsp;:1ml/6-8kg thể trọng.</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG THUỐC:</strong>&nbsp;7 ng&agrave;y trước khi giết mổ.</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:47:51', NULL),
(45, 12, 'W-ENZO 20% ORAL', 'w-enzo-20-oral', 'W-ENZO 20% ORAL – ĐẶC TRỊ CÁC BỆNH NHIỄM TRÙNG ĐƯỜNG TIÊU HOÁ VÀ ĐƯỜNG HÔ HẤP', 0.00, NULL, '<p><strong>THUỐC BỘT PHA TI&Ecirc;M</strong></p>\r\n\r\n<p><strong>W-ENZO 20% ORAL &ndash; ĐẶC TRỊ C&Aacute;C BỆNH NHIỄM TR&Ugrave;NG ĐƯỜNG TI&Ecirc;U HO&Aacute; V&Agrave; ĐƯỜNG H&Ocirc; HẤP</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Enrofloxacine &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm tr&ugrave;ng đường ti&ecirc;u ho&aacute;, h&ocirc; hấp, nhiễm tr&ugrave;ng huyết, tiết niệu, nhiễm tr&ugrave;ng vết thương tr&ecirc;n b&ecirc;, cừu, ngh&eacute;, d&ecirc;, lợn, da cầm</p>\r\n\r\n<p><strong>CHỈ ĐỊNH CỦA BSTY</strong>: Đặc trị c&aacute;c bệnh nhiễm tr&ugrave;ng đường ti&ecirc;u ho&aacute; v&agrave; đường h&ocirc; hấp do c&aacute;c vi sinh vật nhạy cảm với Enrofloxacine g&acirc;y ra như vi&ecirc;m phổi, nhiễm tr&ugrave;ng Mycoplasmosis, nhiễm tr&ugrave;ng huyết, Coli ti&ecirc;u chảy, tụ huyết tr&ugrave;ng, thương h&agrave;n v&agrave; sốt ph&oacute; thương h&agrave;n, tiết niệu, nhiễm tr&ugrave;ng đường ti&ecirc;u ho&aacute;, nhiễm tr&ugrave;ng vết thương v&agrave; nhiễm tr&ugrave;ng do vi khuẩn thứ cấp như biến chứng của bệnh do virut của gia cầm, lợn, b&ecirc;, ngh&eacute;, cừu v&agrave; d&ecirc;</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:</strong></p>\r\n\r\n<p><strong>Heo</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1ml/20-25kg thể trọng</p>\r\n\r\n<p><strong>B&ecirc;, cừu, d&ecirc;</strong>&nbsp; &nbsp; : 5ml/75-150kg thể trọng</p>\r\n\r\n<p><strong>Gia cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;:<strong>&nbsp;1ml/10-15kg thể trọng</strong></p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG THUỐC:</strong>&nbsp;7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:48:27', NULL),
(46, 13, 'POVIDIN', 'povidin', 'POVIDIN – TÁC DỤNG NHANH, MẠNH, DIỆT KHUẨN VIRUS, KHÔNG ĂN MÒN KIM LOẠI', 0.00, NULL, '<p><strong>THUỐC BỘT</strong></p>\r\n\r\n<p><strong>POVIDIN &ndash; T&Aacute;C DỤNG NHANH, MẠNH, DIỆT KHUẨN VIRUS, KH&Ocirc;NG ĂN M&Ograve;N KIM LOẠI</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Povidone iodine &amp; Dung m&ocirc;i vừa đủ</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Diệt vi khuẩn, nấm, vi r&uacute;t, động vật nguy&ecirc;n sinh. Khử tr&ugrave;ng chuồng trại, dụng cụ, phương tiện vận chuyển, khử tr&ugrave;ng &acirc;m đạo, tử cung, s&aacute;t tr&ugrave;ng vết thương, vết mổ tr&ecirc;n gia s&uacute;c, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p>S&aacute;t tr&ugrave;ng chuồng trại, dụng cụ, phương tiện vận chuyển: 1l/100-250l nước phun đều l&ecirc;n c&aacute;c bề mặt chuồng nu&ocirc;i, dụng cụ chăn nu&ocirc;i v&agrave; m&ocirc;i trường xung quanh</p>\r\n\r\n<p>Khủ tr&ugrave;ng nguồn nước: 2l/300m3</p>\r\n\r\n<p>Thụt rửa tử cung v&agrave; v&uacute;: 10ml/5l nước</p>\r\n\r\n<p>S&aacute;t tr&ugrave;ng vết thương, vết mổ: 20ml/2.5l nước</p>\r\n\r\n<p>S&aacute;t tr&ugrave;ng m&aacute;y ấp trứng: 10ml/5l nước</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: kh&ocirc;ng cần thiết</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:49:22', NULL),
(47, 13, 'W-MEN HÚT MÙI', 'w-men-hut-mui', 'W-MEN HÚT MÙI – MEN VI SINH BẮT MÙI, HIỆU QUẢ VƯỢT TRỘI, HẾT MÙI NHANH CHỈ SAU VÀI PHÚT', 0.00, NULL, '<p><strong>DẠNG LỎNG</strong></p>\r\n\r\n<p><strong>W-MEN H&Uacute;T M&Ugrave;I &ndash; MEN VI SINH BẮT M&Ugrave;I, HIỆU QUẢ VƯỢT TRỘI, HẾT M&Ugrave;I NHANH CHỈ SAU V&Agrave;I PH&Uacute;T</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:&nbsp;</strong>&nbsp;Bacillus subtillis, Natri, Kali &amp; T&aacute; dược độc quyền.</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG</strong>:&nbsp;</p>\r\n\r\n<p>Phun: Trong chuồng nu&ocirc;i đang c&oacute; vật nu&ocirc;i v&agrave; chuồng nu&ocirc;i th&uacute; cảnh, qu&acirc;y &uacute;m g&agrave;, vịt,&hellip;</p>\r\n\r\n<p>Phun: Trong kho để trứng, khay trứng, trong l&ograve; ấp, vỏ trứng sau khi ra l&ograve;</p>\r\n\r\n<p>Phun: Xử l&yacute; r&aacute;c thải hữu cơ chăn nu&ocirc;i, sinh hoạt, cống r&atilde;nh, ph&acirc;n gia s&uacute;c, gia cầm. Xử l&yacute; phế thải n&ocirc;ng nghiệp l&agrave;m ph&acirc;n b&oacute;n hữu cơ vi sinh</p>\r\n\r\n<p>Giảm kh&iacute; độc, tăng lượng oxy trong chuồng nu&ocirc;i</p>\r\n\r\n<p>Chuồng nu&ocirc;i kh&ocirc; sạch, giảm m&ugrave;i. Vật nu&ocirc;i ti&ecirc;u ho&aacute; t&oacute;t, giảm bệnh, lớn nhanh khoẻ mạnh</p>\r\n\r\n<p><strong>C&Aacute;CH D&Ugrave;NG V&Agrave; LIỀU LƯỢNG</strong>:</p>\r\n\r\n<p>Pha sản phẩm với nước sạch v&agrave; phun</p>\r\n\r\n<p>Liều d&ugrave;ng:</p>\r\n\r\n<ul>\r\n	<li>Trong chồng: Định kỳ 15-20 ng&agrave;y phun một lần. Khi cần thiết th&igrave; phun 3 ng&agrave;y li&ecirc;n tiếp. Khi phun pha 1 l&iacute;t sản phẩm với 30 l&iacute;t nước sạch &ndash; phun 200m2</li>\r\n	<li>Ngo&agrave;i chuồng: Định kỳ 30 ng&agrave;y phun 1 lần. Khi cần thiết th&igrave; phun 5 ng&agrave;y li&ecirc;n tiếp. Khi phun pha 1 l&iacute;t sản phẩm với 30 l&iacute;t nước sạch &ndash; phun 300-500 m2</li>\r\n	<li>Bể biogas: 1 l&iacute;t/50m3</li>\r\n	<li>Xử l&yacute; r&aacute;c thải hữu cơ: 1l&iacute;t sản phẩm d&ugrave;ng cho 1-3 tấn r&aacute;c thải, ph&acirc;n.</li>\r\n</ul>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh s&aacute;ng trực tiếp, nhiệt độ m&aacute;t dưới 32&deg;C.</p>\r\n\r\n<p><strong>LƯU &Yacute;</strong>: Sản phẩm dạng men vi sinh sống n&ecirc;n chai sẽ sinh hơi v&agrave; căng phồng nếu đẻ ở nhiệt độ tr&ecirc;n 35 độ</p>\r\n', 0, '2025-11-12 15:50:05', NULL),
(48, 18, 'DOXY MAX 500', 'doxy-max-500', 'DOXY MAX 500 – ĐẶC TRỊ CÁC BỆNH ĐƯỜNG HÔ HẤP, BỆNH VIÊM PHỔI, CRD, CCRD, HEN GHÉP E.COLI, VIÊM RUỘT TIÊU CHẢY, TỤ HUYẾT TRÙNG', 0.00, NULL, '<p><strong>THUỐC BỘT</strong></p>\r\n\r\n<p><strong>DOXY MAX 500 &ndash; ĐẶC TRỊ C&Aacute;C BỆNH ĐƯỜNG H&Ocirc; HẤP, BỆNH VI&Ecirc;M PHỔI, CRD, CCRD, HEN GH&Eacute;P E.COLI, VI&Ecirc;M RUỘT TI&Ecirc;U CHẢY, TỤ HUYẾT TR&Ugrave;NG</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Doxycycline hyclate &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị nhiễm tr&ugrave;ng đường h&ocirc; hấp tr&ecirc;n lợn, g&agrave;</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Gia cầm v&agrave; thuỷ cầm</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/25-30kg thể trọng</p>\r\n\r\n<p><strong>Heo</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1g/50-60kg thể trọng</p>\r\n\r\n<p><strong>B&ecirc;, ngh&eacute;, d&ecirc;, cừu&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/90-100kg thể trọng</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:51:28', NULL),
(49, 18, 'W-ETHOCIN 50', 'w-ethocin-50', 'W-ETHOCIN 50 – ĐẶC TRỊ BỆNH ĐƯỜNG HÔ HẤP HEN, HEN CRD, CCRD, ORT, BỆNH TỤ HUYẾT TRÙNG, BỆNH ĐƯỜNG TIÊU HOÁ E.COLI, THƯƠNG HÀN', 0.00, NULL, '<p><strong>THUỐC BỘT</strong></p>\r\n\r\n<p><strong>W-ETHOCIN 50 &ndash; ĐẶC TRỊ BỆNH ĐƯỜNG H&Ocirc; HẤP HEN, HEN CRD, CCRD, ORT, BỆNH TỤ HUYẾT TR&Ugrave;NG, BỆNH ĐƯỜNG TI&Ecirc;U HO&Aacute; E.COLI, THƯƠNG H&Agrave;N</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:&nbsp;</strong>Erythromycin, Colistin sulfate &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:&nbsp;</strong>Trị nhiễm khuẩn đường ti&ecirc;u ho&aacute; tr&ecirc;n tr&acirc;u, b&ograve;, ngựa, lợn, cừu, d&ecirc;, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Gia cầm, thuỷ sản</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 1g/4-6l nước hoặc 1g/20-30kg thể trọng/ng&agrave;y</p>\r\n\r\n<p><strong>Gia s&uacute;c&nbsp; &nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/6-8l nước hoặc 1g/30-40kg thể trọng/ng&agrave;y</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH</strong>: Kh&ocirc;ng d&ugrave;ng cho vật nu&ocirc;i mẫn cảm với th&agrave;nh phần của thuốc</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:52:02', NULL),
(50, 18, 'WINQUINOL 600', 'winquinol-600', 'WINQUINOL 600 – ĐIỀU TRỊ VIÊM LƯỠI MẠN TÍNH, ĐIỀU TRỊ BỆNH TIÊU CHẢY, NHIỄM TRÙNG DO NẤM MỐC GÂY RA', 0.00, NULL, '<p><strong>THUỐC BỘT</strong></p>\r\n\r\n<p><strong>WINQUINOL 600 &ndash; ĐIỀU TRỊ VI&Ecirc;M LƯỠI MẠN T&Iacute;NH, ĐIỀU TRỊ BỆNH TI&Ecirc;U CHẢY, NHIỄM TR&Ugrave;NG DO NẤM MỐC G&Acirc;Y RA</strong></p>\r\n\r\n<p><strong>TH&Agrave;NH PHẦN:</strong>&nbsp;Halquinol &amp; T&aacute; dược độc quyền</p>\r\n\r\n<p><strong>C&Ocirc;NG DỤNG:</strong>&nbsp;Trị ti&ecirc;u chảy tr&ecirc;n lợn, gia cầm</p>\r\n\r\n<p><strong>LIỀU LƯỢNG V&Agrave; C&Aacute;CH D&Ugrave;NG:&nbsp;</strong></p>\r\n\r\n<p><strong>Gia cầm, thuỷ sản</strong>&nbsp; &nbsp; &nbsp; &nbsp;: 1g/20-30kg thể trọng</p>\r\n\r\n<p><strong>Heo</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: 1g/40-60kg thể trọng</p>\r\n\r\n<p><strong>LƯU &Yacute;:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng qu&aacute; liều quy định.</p>\r\n\r\n<p><strong>CHỐNG CHỈ ĐỊNH:</strong>&nbsp;Kh&ocirc;ng d&ugrave;ng cho th&uacute; mẫn cảm với th&agrave;nh phần của thuốc.</p>\r\n\r\n<p><strong>THỜI GIAN NGỪNG SỬ DỤNG THUỐC</strong>: 7 ng&agrave;y trước khi giết mổ</p>\r\n\r\n<p><strong>BẢO QUẢN:</strong>&nbsp;Nơi kh&ocirc; tho&aacute;ng, nhiệt độ kh&ocirc;ng qu&aacute; 30&deg;C.</p>\r\n', 0, '2025-11-12 15:55:51', NULL);

